            $blogCategory = \App\Models\BlogCategory::where('slug', '=', $post['sec_url'])->first();
            $blogCategoryId = $blogCategory->id;

            $newArticles[] = [
                'name_ru' => $post['title_ru'],
                'name_ua' => null,
                'category_id' => $blogCategoryId,
                'user_id' => 42, // need to import user_table
                'description_ru' => $post['description'],
                'description_ua' => null,
                'image_id' => $imageId,
                'meta_id' => $metaId,
                'content_ru' => $post['post_text'],
                'content_ua' => null,
                'rating' => $post['rating'],
                'slug' => $post['url_address'] . '.html',
                'views' => $post['views'],
                'published_at' => null,
                'created_at' =>  $post['date'],
                'updated_at' => null,
                'status' => 0,
                'votes' => null
            ];
        }
        \Illuminate\Support\Facades\DB::table('blog')->insert($newArticles);
    }
}
